function gameInit(){board=[0,1,2,3,4,5,6,7,8],$(".square").text(""),gameStatus(""),gameOver=!1}function squareClick(e){"number"!=typeof board[e]||gameOver||(updateBoard(humanToken,e,"human"),drawCheck()||gameOver||updateBoard(aiToken,aiMove(),"ai")),console.log(board)}function updateBoard(e,a,r){$("#"+a).text(e),board[a]=e,winCheck(board,e)&&(gameStatus("human"===r?"You Win!":"You Lose!"),gameOver=!0)}function gameStatus(e){$(".status").text(e)}function winCheck(e,a){for(var r=0;r<8;r++)for(var n=0;n<3&&e[termStates[r][n]]===a;n++)if(2===n)return!0}function drawCheck(){if(!availableMoves(board).length)return gameStatus("It's A Draw!"),!0}function availableMoves(e){for(var a=[],r=0;r<e.length;r++)"number"==typeof e[r]&&a.push(e[r]);return a}function aiMove(){return minimax(board,aiToken).index}function minimax(e,a){var r=availableMoves(e);if(winCheck(e,humanToken))return{score:-10};if(winCheck(e,aiToken))return{score:10};if(!r.length)return{score:0};for(var n=[],o=0;o<r.length;o++){var t={};if(t.index=e[r[o]],e[r[o]]=a,a===aiToken){var i=minimax(e,humanToken);t.score=i.score}else{var i=minimax(e,aiToken);t.score=i.score}e[r[o]]=t.index,n.push(t)}var u;if(a===aiToken)for(var s=-1/0,o=0;o<n.length;o++)n[o].score>s&&(s=n[o].score,u=o);else for(var s=1/0,o=0;o<n.length;o++)n[o].score<s&&(s=n[o].score,u=o);return n[u]}var board;const termStates=[[0,1,2],[3,4,5],[6,7,8],[0,3,6],[1,4,7],[2,5,8],[0,4,8],[2,4,6]];var humanToken="O",aiToken="X",gameOver=!1;gameInit(),$(".square").click(function(e){squareClick(e.target.id)});