function gameInit(){board=[0,1,2,3,4,5,6,7,8],$(".square").text(""),gameStatus(""),gameOver=!1}function squareClick(a){"number"!=typeof board[a]||gameOver||(updateBoard(humanToken,a,"human"),drawCheck()||gameOver||updateBoard(aiToken,aiMove(),"ai")),console.log(board)}function updateBoard(a,e,t){$("#"+e).text(a),board[e]=a,winCheck(board,a)&&(gameStatus("human"===t?"You Win!":"You Lose!"),gameOver=!0)}function winCheck(a,e){for(var t=0;t<8;t++)for(var o=0;o<3&&a[termStates[t][o]]===e;o++)if(2===o)return!0}function drawCheck(){if(console.log(availableMoves()),!availableMoves().length)return gameStatus("It's A Draw!"),!0}function gameStatus(a){$(".status").text(a)}function availableMoves(){for(var a=[],e=0;e<board.length;e++)"number"==typeof board[e]&&a.push(board[e]);return a}function aiMove(){return availableMoves()[0]}var board;const termStates=[[0,1,2],[3,4,5],[6,7,8],[0,3,6],[1,4,7],[2,5,8],[0,4,8],[2,4,6]];var humanToken="O",aiToken="X",gameOver=!1;gameInit(),$(".square").click(function(a){squareClick(a.target.id)});